import React from 'react'
import { Stack,Box,Typography } from '@mui/material'
import { useState,useEffect } from 'react'
import Sidebar from './Sidebar'
import Videos from './Videos'
import {FetchApi} from '../utils/FetchApi'
const Feed = () => {
const [selectedCategory, setSelectedCategory] = useState('New')
const [videos, setVideos] = useState([])
useEffect(()=>{
  FetchApi(`search?part=snippet&q=${selectedCategory}`).then((data)=>setVideos(data.items))
},[selectedCategory]);

  return (
    <Stack sx={{flexDirection: {xs:"column",md:"row"}}}>

      <Box sx={{height:{sx:"auto",md:"92vh"},borderRight:"1px solid #3d3d3d",px:{sx:0,md:1}}}>

      
      <Sidebar
      selectedCategory={selectedCategory}
      setSelectedCategory={setSelectedCategory}
      />

        <Typography className='copyrigth' variant='body2' sx={{mt:1.5,color:'#fff'}}>copyrigth 2023 MSD Media</Typography>
      </Box>

      <Box p={2} sx={{overflowY:'auto',height:'90vh',flex:2}}>
      <Typography variant='h4' mb={2} fontWeight='bold' sx={{color:'WHITE'}} >
        {selectedCategory} <span style={{color:'#FC2503'}} >videos</span>
      </Typography>

      <Videos videos={videos}/>
      </Box>

    
     
    </Stack>
  )
}

export default Feed
