import React from 'react'
import { useState,useEffect } from 'react'
import { useParams } from 'react-router-dom'
import {ChannelCard} from './ChannelCard'
import {Videos} from './Videos'
import {FetchApi} from '../utils/FetchApi'

import { Box,Typography } from '@mui/material'

const ChannelDetails = () => {

  const [channelDetail,setChannelDetail]=useState(null)
  const [videos,setVideos]=useState([])
  
  const {id}=useParams();

  useEffect(()=>{
    FetchApi(`channels?part=snippet&id=${id}`).then((data)=>setChannelDetail(data?.items[0]))
    FetchApi(`search?channelId=${id}&part=snippet&order=date`).then((data)=>setVideos(data?.items))
  },[id])

  console.log(channelDetail,videos)
  return(

        <Box minHeight='95vh'>
          <Box>
            <div style={{background: 'linear-gradient(90deg, rgba(9,19,121,1) 0%, rgba(36,16,0,1) 0%, rgba(255,0,0,1) 100%)',heig
             }}
             />
          </Box>
        </Box>
  )
}



export default ChannelDetails
