import React, { useState,useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Box,Stack,Typography } from '@mui/material'
import ReactPlayer from 'react-player'
import { useParams } from 'react-router-dom'
import Videos from './Videos'
import {FetchApi} from '../utils/FetchApi'



const VideoDetails = () => {
  const [VideoDetail,setVideoDetail]=useState(null);
  const {id}=useParams()

  useEffect(()=>{
    FetchApi(`videos?part=snippet,statistics&id=${id}`).then((data)=>setVideoDetail(data.items[0]))
  },[id])

  const{snippet}=VideoDetail
  
  return (
   <Box minHeight='95vh'>
    <Stack direction={{ xs:'column',md:'row' }}>
    <Box flex={1}>
    <Box sx={{ width:'100%',position:'sticky',top:'86px' }} >

      <ReactPlayer className="react-player" controls url={`https://www.youtube.com/watch?v=${id}`}
      />

       <Typography color='#fff' variant='h5' fontWeight='bold' p={2}>{snippet}</Typography>

    </Box>
    </Box>
    </Stack>


   </Box>
  )
}

export default VideoDetails
